import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";
import {
  Typography,
  Card,
  CardContent,
  Grid,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormLabel,
  FormControl
} from "@material-ui/core";

const useStyles = makeStyles(theme => ({
  paper: {
    padding: theme.spacing(1),
    minWidth: 200
  },
  formControl: {
    margin: theme.spacing(3)
  }
}));

export default function Acceuil() {
  const classes = useStyles();
  const [data, setData] = useState([]);
  const [Prix, setPrix] = React.useState("female");

  const handleChange = event => {
    setPrix(event.target.value);
  };

  useEffect(() => {
    axios.post("http://localhost:4000/articles/recherche").then(resultat => {
      setData(resultat.data);
    });
  }, []);

  return (
    <Grid container direction="row" >
      <Grid item driection="column">
        <FormControl component="fieldset" className={classes.formControl}>
          <FormLabel component="legend">Prix</FormLabel>
          <RadioGroup
            aria-label="Prix"
            name="prix"
            value={prix}
            onChange={handleChange}
          >
            <FormControlLabel value="Tout" control={<Radio />} label="Tout" />
            <FormControlLabel value="inferieur à 25" control={<Radio />} label="inferieur à 25" />
            <FormControlLabel value="entre 25 et 50" control={<Radio />} label="entre 25 et 50" />
            <FormControlLabel value="entre 50 et 100" control={<Radio />} label="entre 50 et 100" />
            <FormControlLabel value="plus que 100" control={<Radio />} label="plus que 100" />
          </RadioGroup>
        </FormControl>
      </Grid>
      <Grid item className={classes.formControl}>
        {data.map(d => (
          <Grid item xs={2} direction="row" className={classes.paper}>
            <Card>
              <CardContent>
                <Typography>{d.nom}</Typography>
                <Typography>{d.prix} TND</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Grid>
  );
}
